mapper.select:
SELECT qs.id AS submitId, u.id AS userId, u.userName AS userName, u.userAccount AS userAccount, u.userAvatar AS userAvatar, q.id AS questionId, q.title AS questionTitle, qs.language AS language, qs.status as status, CONCAT(ROUND(CHAR_LENGTH(qs.code) / 1024, 2), ' KB') AS codeLength, qs.createTime AS submitTime, NULL AS timeUse, NULL AS memoryUse, NULL AS message, qs.judgeInfo AS judgeInfo, qs.judgeMessages As judgeMessages, qs.`code` AS `code` FROM question_submit qs LEFT JOIN user u ON qs.userId = u.id LEFT JOIN question q ON qs.questionId = q.id
AND u.userName LIKE CONCAT('%', #{userName}, '%')
AND q.title LIKE CONCAT('%', #{questionTitle}, '%')
AND #{submitStatus} = qs.status
AND qs.language = #{language}
ORDER BY qs.createTime DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT qs.id AS submitId, u.id AS userId, u.userName AS userName, u.userAccount AS userAccount, u.userAvatar AS userAvatar, q.id AS questionId, q.title AS questionTitle, qs.language AS language, qs.status as status, CONCAT(ROUND(CHAR_LENGTH(qs.code) / 1024, 2), ' KB') AS codeLength, qs.createTime AS submitTime, NULL AS timeUse, NULL AS memoryUse, NULL AS message, qs.judgeInfo AS judgeInfo, qs.judgeMessages As judgeMessages, qs.`code` AS `code` FROM question_submit qs LEFT JOIN user u ON qs.userId = u.id LEFT JOIN question q ON qs.questionId = q.id
AND qs.userId = #{userId}
AND q.title LIKE CONCAT('%', #{questionTitle}, '%')
AND #{submitStatus} = qs.status
AND qs.language = #{language}
ORDER BY qs.createTime DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM question_submit qs LEFT JOIN user u ON qs.userId = u.id LEFT JOIN question q ON qs.questionId = q.id
AND qs.userId = #{userId}
AND q.title LIKE CONCAT('%', #{questionTitle}, '%')
AND #{submitStatus} = qs.status
AND qs.language = #{language}
